mapper.select:
INSERT INTO trade_rules ( uid, sid, today_or_tomorrow, buy_or_sell, trade_timing_type, in_or_exit, order_by ) VALUES (
#{rule.uid}, #{rule.sid}, #{rule.todayOrTomorrow}, #{rule.buyOrSell}, #{rule.tradeTimingType}, #{rule.inOrExit}, #{rule.orderBy}
) RETURNING rid

update.foreach:
UPDATE trade_rules
SET
  today_or_tomorrow = #{rule.todayOrTomorrow},
  buy_or_sell = #{rule.buyOrSell},
  trade_timing_type = #{rule.tradeTimingType},
  in_or_exit = #{rule.inOrExit},
  order_by = #{rule.orderBy}
WHERE uid = #{rule.uid}
  AND rid = #{rule.rid}

mapper.delete:
DELETE FROM trade_rules
WHERE
uid = #{uid}
AND
rid IN

  #{rid}
