mapper.select:
INSERT INTO trade_strategy_palettes ( uid, rid, left_joint_type, right_joint_type, nest_open, nest_close, order_by ) VALUES (
#{palette.uid}, #{palette.rid}, #{palette.leftJointType}, #{palette.rightJointType}, #{palette.nestOpen}, #{palette.nestClose}, #{palette.orderBy}
) RETURNING pid

update.foreach:
UPDATE trade_strategy_palettes
SET
  left_joint_type = #{palette.leftJointType},
  right_joint_type = #{palette.rightJointType},
  nest_open = #{palette.nestOpen},
  nest_close = #{palette.nestClose},
  order_by = #{palette.orderBy}
WHERE uid = #{palette.uid}
  AND pid = #{palette.pid}

mapper.delete:
DELETE FROM trade_strategy_palettes
WHERE
uid = #{uid}
AND
rid IN

  #{pid}
